import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import MerchantDashboard from './components/MerchantDashboard';
import UserDashboard from './components/UserDashboard';
import MerchantAnalytics from './components/MerchantAnalytics';
import './styles/App.css';

const ProtectedRoute = ({ children, isMerchant }) => {
  const token = localStorage.getItem('token');
  const userIsMerchant = localStorage.getItem('isMerchant') === 'true';

  if (!token) {
    return <Navigate to="/login" />;
  }

  if (isMerchant && !userIsMerchant) {
    return <Navigate to="/user" />;
  }

  if (!isMerchant && userIsMerchant) {
    return <Navigate to="/merchant" />;
  }

  return children;
};

const App = () => {
  return (
    <Router>
      <div className="app">
        <nav className="navbar">
          <div className="nav-links">
            <a href="/merchant">Merchant Dashboard</a>
            <a href="/user">User Dashboard</a>
            <a href="/analytics">Analytics</a>
          </div>
          <div className="auth-links">
            <a href="/login">Login</a>
          </div>
        </nav>

        <div className="content">
          <Routes>
            <Route path="/login" element={<Login />} />

            <Route path="/merchant" element={
              <ProtectedRoute isMerchant={true}>
                <MerchantDashboard />
              </ProtectedRoute>
            } />

            <Route path="/user" element={
              <ProtectedRoute isMerchant={false}>
                <UserDashboard />
              </ProtectedRoute>
            } />

            <Route path="/analytics" element={
              <ProtectedRoute isMerchant={true}>
                <MerchantAnalytics />
              </ProtectedRoute>
            } />

            <Route path="/" element={<Navigate to="/login" />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
