import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserDashboard = () => {
    const [installments, setInstallments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchInstallments = async () => {
            try {
                const response = await axios.get('/api/plans/');
                setInstallments(response.data);
            } catch (error) {
                console.error('Error fetching installments:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchInstallments();
    }, []);

    const handlePayment = async (id) => {
        try {
            await axios.post(`/api/installments/${id}/pay/`);
            setInstallments((prev) =>
                prev.map((installment) =>
                    installment.id === id ? { ...installment, status: 'Paid' } : installment
                )
            );
        } catch (error) {
            console.error('Error processing payment:', error);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>User Dashboard</h1>
            <table>
                <thead>
                    <tr>
                        <th>Installment Amount</th>
                        <th>Due Date</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {installments.map((installment) => (
                        <tr key={installment.id}>
                            <td>{installment.amount} ريال</td>
                            <td>{new Date(installment.due_date).toLocaleDateString()}</td>
                            <td style={{ color: installment.status === 'Paid' ? 'green' : 'red' }}>
                                {installment.status}
                            </td>
                            <td>
                                {installment.status === 'Pending' && (
                                    <button onClick={() => handlePayment(installment.id)}>Pay Now</button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserDashboard;
